<pre class="lang:typescript decode:true">
private _onNavClick(e: React.MouseEvent<HTMLElement>, item: INavLink): void {
    switch(item.key){
      case "key8":
        this._onEdit();
        break;
      case "key9":
        this._onDelete();
        break;
      default:
        this.setState({showPlaylistMenu: false});
        break;
    }
  }
